INSERT INTO `statics_multi` VALUES ('1', 'Cuối trang', null, 'cuoi-trang', '', '<p><b>Siêu thị Nam Đô&nbsp;- Lựa chọn tuyệt vời cho bạn</b><br />\r\n<em>Trang bán hàng của Hội Thiện nguyện Nam Đô. Toàn bộ lợi nhuận sẽ được chuyển cho các hoạt động thiện nguyện.</em><br />\r\nĐịa chỉ: 609 Trương Định, Hoàng Mai, Hà Nội, Việt Nam<br />\r\nĐiện thoại:&nbsp;<br />\r\nEmail: sieuthinamdo@gmail.com<br />\r\nWebsite: www.sieuthinamdo.com<br />\r\nFacebook: <a href=\"http://www.facebook.com/phukientuanphong\" rel=\"nofollow\" target=\"_blank\">www.facebook.com/</a>sieuthinamdo</p>', '1207209822', '0', '0', '1');
INSERT INTO `statics_multi` VALUES ('2', 'Liên hệ', null, 'lien-he', '', '<p>Tuanphong.vn</p>', '1398530302', '0', '0', '1');
INSERT INTO `statics_multi` VALUES ('3', 'Chính sách bảo hành', null, 'chinh-sach-bao-hanh', '', '<p>Chính sách bảo hành sản phẩm tại Tuanphong.vn</p>\r\n\r\n<p>Các quy định bảo hành đối với sản phẩm bán ra tại shop Tuanphong.vn. Quý khách đọc kỹ trước khi mua hàng để hiểu rõ quyền lợi và nghĩa vụ của mình.</p>\r\n\r\n<p>1. Điều kiện bảo hành :</p>\r\n\r\n<p>1.1. Tất cả các sản phẩm phải có tem bảo hành của Tuanphong.vn, sản phẩm còn trong thời gian bảo hành sẽ được bảo hành và hỗ trợ đúng quy định của Tuanphong</p>\r\n\r\n<p>+ Sản phẩm có tem bảo hành và còn thời hạn bảo hành của Shop</p>\r\n\r\n<p>+ Sản phẩm có phiếu bảo hành của Shop</p>\r\n\r\n<p>+ Sản phẩm còn nguyên vẹn, không sứt mẻ, gãy vỡ, biến dạng, bong tróc,...</p>\r\n\r\n<p>(Nếu sản phẩm của bạn không mua tại Shop, vui lòng liên hệ để được hỗ trợ gửi bảo hành nếu là sản phẩm chính hãng).</p>\r\n\r\n<p>- Chế độ bảo hành:</p>\r\n\r\n<p>+ Được bảo hành theo các quy định của hãng sản xuất</p>\r\n\r\n<p>+ Thời gian bảo hành áp dụng theo quy định của Shop tại thời điểm mua hàng</p>\r\n\r\n<p>- Hướng dẫn bảo hành:</p>\r\n\r\n<p>+ Liên hệ trực tiếp qua số điện thoại sau: 097 188 1886 / 0983 6868 59 để thông báo.</p>\r\n\r\n<p>+ Mang trực tiếp qua cửa hàng tại địa điểm sau:</p>\r\n\r\n<p>Địa chỉ: Số 51 Lê Đại Hành (gần vincom Bà Triệu), quận Hai Bà Trưng, Hà Nội</p>\r\n\r\n<p>+ Nếu quý khách ở tỉnh vui lòng gửi chuyển phát về Shop theo địa chỉ: Nguyễn Trọng Phong - Tầng 5 Số 51 Lê Đại Hành, quận Hai Bà Trưng, Hà Nội</p>', '1457751424', '1', '0', '1');
