-- ----------------------------
-- Table structure for tours
-- ----------------------------
DROP TABLE IF EXISTS `tours`;
CREATE TABLE `tours` (
  `tou_id` int(11) NOT NULL AUTO_INCREMENT,
  `tou_title` varchar(255) DEFAULT NULL,
  `tou_code` varchar(255) DEFAULT NULL,
  `tou_search` text CHARACTER SET utf8 COLLATE utf8_bin,
  `tou_category_id` int(11) DEFAULT '0',
  `tou_topic` varchar(255) DEFAULT NULL,
  `tou_old_price` double DEFAULT '0',
  `tou_sale_price` double DEFAULT '0',
  `tou_old_price_child` double DEFAULT '0',
  `tou_sale_price_child` double DEFAULT '0',
  `tou_source_id` int(11) DEFAULT '0',
  `tou_times_id` int(11) DEFAULT '0',
  `tou_time_text` varchar(255) DEFAULT NULL,
  `tou_text_promotion` varchar(255) DEFAULT NULL,
  `tou_daily` tinyint(4) DEFAULT '0',
  `tou_hot` tinyint(4) DEFAULT '0',
  `tou_show_home` tinyint(4) DEFAULT '0',
  `tou_active` tinyint(4) DEFAULT '0',
  `tou_promotion` tinyint(4) DEFAULT '0',
  `tou_meta_title` varchar(255) DEFAULT NULL,
  `tou_meta_keyword` varchar(255) DEFAULT NULL,
  `tou_meta_description` text,
  `tou_short_description` text,
  `tou_description` text,
  `tou_utilities` text,
  `tou_vehicle` text,
  `tou_destination` varchar(255) DEFAULT NULL,
  `tou_picture` varchar(255) DEFAULT NULL,
  `tou_picture_json` text,
  `tou_service_highlight_json` text,
  `tou_link_video` varchar(255) DEFAULT NULL,
  `tou_banner` varchar(255) DEFAULT NULL,
  `tou_background` varchar(255) DEFAULT NULL,
  `admin_id` int(11) DEFAULT '0',
  `lang_id` tinyint(4) DEFAULT '0',
  `tou_create_time` int(11) DEFAULT '0',
  `tou_update_at` int(11) DEFAULT '0',
  `tou_hotline` varchar(255) DEFAULT NULL,
  `tou_name_hotline` varchar(255) DEFAULT NULL,
  `tou_order` int(11) DEFAULT '0',
  `tou_name_supplier` varchar(255) DEFAULT NULL,
  `tou_phone_supplier` varchar(255) DEFAULT NULL,
  `tou_email_supplier` varchar(255) DEFAULT NULL,
  `tou_address_supplier` varchar(255) DEFAULT NULL,
  `tou_price_include` text,
  `tou_schedule` text,
  `tou_phuthu` text,
  PRIMARY KEY (`tou_id`),
  UNIQUE KEY `tou_code` (`tou_code`) USING BTREE,
  KEY `tou_sale_price` (`tou_sale_price`) USING BTREE,
  KEY `tou_source_id` (`tou_source_id`) USING BTREE,
  KEY `tou_daily` (`tou_daily`) USING BTREE,
  KEY `tou_active` (`tou_active`) USING BTREE,
  KEY `tou_order` (`tou_order`) USING BTREE,
  KEY `tou_hot` (`tou_hot`) USING BTREE,
  KEY `tou_show_home` (`tou_show_home`) USING BTREE,
  KEY `tou_create_time` (`tou_create_time`) USING BTREE,
  KEY `tou_category_id` (`tou_category_id`) USING BTREE,
  KEY `tou_times_id` (`tou_times_id`) USING BTREE,
  FULLTEXT KEY `tou_search` (`tou_search`)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

