INSERT INTO `tours` VALUES ('2', 'Tour Singapore - Malaysia 6N5D: Đảo Sentosa - Malacca ', 'BTO102', 0x746F75722073696E6761706F7265202D206D616C617973696120366E35643A20C491E1BAA36F2073656E746F7361202D206D616C616363612020746F75722073696E6761706F7265202D206D616C617973696120366E35643A2064616F2073656E746F7361202D206D616C6163636120, '5', '1,2', '0', '5700000', '0', '4000000', '1', '1', 'Thứ 5 hằng tuần', 'Deal tốt giá shock', '1', '1', '1', '1', '1', '', '', '', '', '', '2,3,4,1', '1', '', '1508156547_vay1508156547.jpg', 'W3sibmFtZSI6IjE1MDgxNTY1NDdfdmF5MTUwODE1NjU0Ny5qcGcifV0=', 'Khách sạn 3 sao | Gồm hành lý ký gửi | Bay VietJet', null, null, null, '1', '0', '1507179401', '1508156586', null, null, '0', '', '', '', '', '', '', '');
INSERT INTO `tours` VALUES ('3', 'Tour Campuchia 4N3D: Siemreap - Phnom Penh 11', 'BTO103', 0x746F75722063616D70756368696120346E33643A207369656D72656170202D2070686E6F6D2070656E682031312020746F75722063616D70756368696120346E33643A207369656D72656170202D2070686E6F6D2070656E6820313120, '3', '1,3', '0', '3700000000', '0', '3000000000', '1', '1', 'Thứ 5 hằng tuần', 'Deal tốt giá shock shock', '1', '1', '1', '1', '1', 'Tour Campuchia 4N3D: Siemreap - Phnom Penh (DEAL)<', 'Tour du lịch Tour Campuchia 4N3D: Siemreap - Phnom Penh (DEAL), du lịch Tour Campuchia 4N3D: Siemreap - Phnom Penh (DEAL), tour Tour Campuchia 4N3D: Siemreap - Phnom Penh (DEAL), tour du lịch Tour Campuchia 4N3D: Siemreap - Phnom Penh (DEAL) giá rẻ, đặt t', 'KH: thứ 5 hằng tuần.\"', '', '', '2,4,1', '1,2', '3,4,1', '1507192926_flj1507192926.jpg', 'W3sibmFtZSI6IjE1MDcxOTI5MjZfbm94MTUwNzE5MjkyNi5qcGcifSx7Im5hbWUiOiIxNTA3MTkyOTI2X3ZtdDE1MDcxOTI5MjYuanBnIn0seyJuYW1lIjoiMTUwNzE5MjkyNl9mbGoxNTA3MTkyOTI2LmpwZyJ9LHsibmFtZSI6IjE1MDcxOTI5MjZfaHlnMTUwNzE5MjkyNi5qcGcifV0=', 'Khách sạn 3 sao | Gồm hành lý ký gửi | Bay VietJet', null, null, null, '1', '0', '1507186822', '1507869083', null, null, '0', 'Công ty du lịch Nam Á Châu ', '', '', '219 - 221 Phạm Ngũ Lão,Q.1,Tp.HCM', '<p>\r\n	<span style=\"font-size:18px;\"><strong>Gi&aacute; Tour bao gồm:</strong></span></p>\r\n<p>\r\n	<span style=\"color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif; font-size: 16px; box-sizing: border-box; text-decoration-line: underline;\">Vận Chuyển</span><span style=\"color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif; font-size: 16px;\">&nbsp;:&nbsp;</span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- V&eacute; m&aacute;y bay khứ hồi TPHCM - Singapore //Kul &ndash; TPHCM (H&agrave;ng kh&ocirc;ng Vietjet Air - Giờ bay: Chuyến đi&nbsp;13h50,Chuyến về 13h00)</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- H&agrave;nh l&yacute; x&aacute;ch tay : 7 kg. H&agrave;nh l&yacute; k&yacute; gửi : 20kg</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Thuế phi trường 2 nước, thuế an ninh, phụ ph&iacute; xăng dầu.&nbsp; &nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Xe du lịch chất lượng cao c&oacute; m&aacute;y lạnh đưa đ&oacute;n tham quan suốt tuyến.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<span style=\"box-sizing: border-box; text-decoration-line: underline;\">Lưu Tr&uacute;</span>&nbsp;:</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Kh&aacute;ch sạn theo ti&ecirc;u chuẩn 3 sao, loại ph&ograve;ng 02 kh&aacute;ch người lớn (ph&ograve;ng 3 kh&aacute;ch được bố tr&iacute; khi cần thiết)</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<span style=\"box-sizing: border-box; text-decoration-line: underline;\">Kh&aacute;c</span>&nbsp;:</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- C&aacute;c bữa ăn theo chương tr&igrave;nh.&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	-&nbsp;V&eacute; tham quan theo chương tr&igrave;nh.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	-&nbsp;Hướng dẫn vi&ecirc;n tiếng Việt suốt tuyến.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	-&nbsp;Qu&agrave; tặng: n&oacute;n du lịch, Bảo hiểm du lịch với mức bồi thường tối đa 210.000.000 VNĐ/kh&aacute;ch/Vụ</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	&nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<span style=\"font-size:18px;\"><strong>Gi&aacute; Tour kh&ocirc;ng bao gồm:</strong></span></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Xe đ&oacute;n tiễn s&acirc;n bay T&acirc;n Sơn Nhất.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Hộ chiếu (c&ograve;n thời hạn tr&ecirc;n 06 th&aacute;ng t&iacute;nh đến ng&agrave;y về).</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- H&agrave;nh l&yacute; qu&aacute; cước qui định (mức tối đa cho ph&eacute;p: 20kg/h&agrave;nh l&yacute; k&yacute; gửi, 7kg/h&agrave;nh l&yacute; x&aacute;ch tay)</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Xe vận chuyển ngo&agrave;i chương tr&igrave;nh + c&aacute;c show về đ&ecirc;m.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Chi ph&iacute; điện thoại, giặt ủi, nước uống trong ph&ograve;ng kh&aacute;ch sạn v&agrave; c&aacute;c chi ph&iacute; c&aacute; nh&acirc;n kh&aacute;c.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Tiền bồi dưỡng cho HDV, t&agrave;i xế (mức đề nghị: 3usd/kh&aacute;ch/ng&agrave;y).</p>\r\n', '<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	NG&Agrave;Y 1 : TP.HCM - SINGAPORE (ĂN TỐI)</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	HDV đ&oacute;n đo&agrave;n&nbsp; tại Lầu 2, Cột số 05 - cổng D1,&nbsp;<strong style=\"box-sizing: border-box;\">Ga đi quốc tế s&acirc;n bay T&acirc;n Sơn Nhất</strong>, l&agrave;m thủ tục đ&aacute;p chuyến bay đi&nbsp;<strong style=\"box-sizing: border-box;\">Singapore</strong>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Đến S&acirc;n bay Changi. Xe v&agrave; HDV Singapore đ&oacute;n v&agrave; đưa đo&agrave;n tham quan:</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Khu vườn sinh th&aacute;i đặc biệt mang t&ecirc;n&nbsp;<strong style=\"box-sizing: border-box;\">Bay South Garden</strong>&nbsp;với hệ thống &ldquo;Si&ecirc;u c&acirc;y&rdquo; khổng lồ lấy năng lượng mặt trời v&agrave;o ban ng&agrave;y v&agrave; tự tỏa s&aacute;ng v&agrave;o ban đ&ecirc;m. Khu vườn nằm trong dự &aacute;n&nbsp;<strong style=\"box-sizing: border-box;\">Gardens by the Bay</strong>, dự &aacute;n c&ocirc;ng vi&ecirc;n sinh th&aacute;i lớn nhất Singapore nhằm n&acirc;ng cao chất lượng cuộc sống, giữ được m&agrave;u xanh cho đ&ocirc; thị sầm uất. To&agrave;n bộ dự &aacute;n c&oacute; tổng diện t&iacute;ch 101 ha, với hơn 250.000 lo&agrave;i thực vật qu&yacute; hiếm.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- C&ocirc;ng vi&ecirc;n Sư Tử Biển - &ldquo;<strong style=\"box-sizing: border-box;\">Merlion Park</strong>&rdquo;.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; text-align: center; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<img alt=\"\" height=\"399\" src=\"https://cdn2.ivivu.com/2017/06/01/16/ivivu-merlion-park.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; margin: 0px auto; display: block; max-width: 100%; height: auto;\" width=\"710\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Chụp ảnh b&ecirc;n ngo&agrave;i&nbsp;<strong style=\"box-sizing: border-box;\">Nh&agrave; H&aacute;t &ldquo;Victoria&rdquo;, T&ograve;a Nh&agrave; Quốc Hội, T&ograve;a Thị Ch&iacute;nh.</strong></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Ăn tối tại nh&agrave; h&agrave;ng địa phương.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Về kh&aacute;ch sạn nhận ph&ograve;ng, nghỉ ngơi.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Hoặc qu&yacute; kh&aacute;ch tự do dạo phố Singapore hoặc cũng c&oacute; thể đăng k&yacute; chương tr&igrave;nh tour: Dạo thuyền tr&ecirc;n s&ocirc;ng Singapore, Kh&aacute;m ph&aacute; hệ thống t&agrave;u điện ngầm hoặc tham quan th&agrave;nh phố phong thủy marina (<strong style=\"box-sizing: border-box;\">Chi ph&iacute; tự t&uacute;c</strong>).</p>\r\n<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	NG&Agrave;Y 2 : SINGAPORE - SENTOSA - JOHOR BHARU &nbsp;(ĂN BA BỮA)</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Ăn s&aacute;ng&nbsp;<strong style=\"box-sizing: border-box;\">Buffet tại kh&aacute;ch sạn</strong>. Xe đưa đo&agrave;n đi tham quan mua sắm tại cửa h&agrave;ng trưng b&agrave;y đ&aacute; qu&yacute; v&agrave; cửa h&agrave;ng dầu gi&oacute; nổi tiếng tại Singapore</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Sau khi ăn trưa, xe đưa kh&aacute;ch v&agrave;o&nbsp;<strong style=\"box-sizing: border-box;\">đảo SENTOSA</strong>&nbsp;tham quan:</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Khu đại dương nh&acirc;n tạo&nbsp;<strong style=\"box-sizing: border-box;\">S.E.A Aquarium</strong>&nbsp;lớn nhất thế giới với hơn 100.000 lo&agrave;i sinh vật biển thuộc 800 lo&agrave;i được tập hợp trong khu đại dương nh&acirc;n tạo v&agrave; c&ograve;n c&oacute; đến 20.000 lo&agrave;i san h&ocirc; c&oacute; sức chứa 45 triệu l&iacute;t nước n&agrave;y. C&oacute; thể kể đến như c&aacute; đuối, c&aacute; mập, c&aacute; heo Th&aacute;i B&igrave;nh Dương...</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; text-align: center; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<img alt=\"\" height=\"398\" src=\"https://cdn2.ivivu.com/2017/06/01/16/ivivu-thuy-cung-see-aquarium-.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; margin: 0px auto; display: block; max-width: 100%; height: auto;\" width=\"710\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- B&agrave;o t&agrave;ng H&agrave;ng Hải (<strong style=\"box-sizing: border-box;\">Maritime Museum</strong>) giới thiệu về lịch sử ng&agrave;nh&nbsp;<strong style=\"box-sizing: border-box;\">h&agrave;ng hải ở Đ&ocirc;ng Nam &Aacute;</strong>&hellip;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Ăn trưa c&aacute;c&nbsp;<strong style=\"box-sizing: border-box;\">m&oacute;n nướng H&agrave;n Quốc</strong>. Xe đưa đo&agrave;n ra cửa khẩu&nbsp;<strong style=\"box-sizing: border-box;\">Tuaslink Checkpoint</strong>&nbsp;khởi h&agrave;nh đi&nbsp;<strong style=\"box-sizing: border-box;\">Johor Bahru Malaysia</strong>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Đến&nbsp;<strong style=\"box-sizing: border-box;\">Johor Bahru</strong>, đo&agrave;n l&agrave;m thủ tục nhập cảnh&nbsp;<strong style=\"box-sizing: border-box;\">Malaysia</strong>. Xe v&agrave; HDV đ&oacute;n đo&agrave;n đi ăn tối tại nh&agrave; h&agrave;ng địa phương. Về nhận ph&ograve;ng kh&aacute;ch sạn &ndash; tự do kh&aacute;m ph&aacute; th&agrave;nh phố Johor Bahru về đ&ecirc;m. Nghỉ đ&ecirc;m tại&nbsp;<strong style=\"box-sizing: border-box;\">Johor Bahru.</strong></p>\r\n<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	NG&Agrave;Y 3 : JOHOR BAHRU &ndash; MALACCA - KUALA LUMPUR (ĂN BA BỮA)&nbsp;</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Ăn s&aacute;ng, l&agrave;m thủ tục trả ph&ograve;ng. Xe đưa đ&ograve;an khởi h&agrave;nh đi&nbsp;<strong style=\"box-sizing: border-box;\">Malacca &ndash; Th&agrave;nh phố cổ c&oacute; lịch sử tr&ecirc;n 500 năm</strong>&nbsp;với c&aacute;c kiến tr&uacute;c đặc trưng của người Trung Hoa, Bồ Đ&agrave;o Nha, H&agrave; Lan. Tham quan v&agrave; chụp ảnh tại:</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Quảng trường H&agrave; Lan với Tuợng đ&agrave;i Victoria, Nh&agrave; thờ th&aacute;nh Paul, Ph&aacute;o đ&agrave;i A&rsquo;famosa.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Chenghoon Temple - Ng&ocirc;i ch&ugrave;a cổ nhất Malaca.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Ăn trưa tại<strong style=\"box-sizing: border-box;\">&nbsp;Malacca</strong>. Xe đưa qu&yacute; kh&aacute;ch khởi h&agrave;nh đi&nbsp;<strong style=\"box-sizing: border-box;\">Kuala Lumpur</strong>. Tham quan:</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; text-align: center; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<img alt=\"\" height=\"399\" src=\"https://cdn2.ivivu.com/2017/06/01/16/ivivu-christ-church-melaka.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; margin: 0px auto; display: block; max-width: 100%; height: auto;\" width=\"710\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	-&nbsp;<strong style=\"box-sizing: border-box;\">Cung điện Ho&agrave;ng Gia</strong>&nbsp;(tham quan chụp h&igrave;nh b&ecirc;n ngo&agrave;i).</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Thắp hương tại&nbsp;<strong style=\"box-sizing: border-box;\">Ch&ugrave;a B&agrave; Thi&ecirc;n Hậu</strong></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	-&nbsp;<strong style=\"box-sizing: border-box;\">Quảng trường Độc Lập</strong>&nbsp;với cột cờ cao nhất thế giới.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Tham quan &amp; mua kẹo tại&nbsp;<strong style=\"box-sizing: border-box;\">Xưởng sản xuất Chocolate</strong>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Xe đưa qu&yacute; kh&aacute;ch tham quan chụp h&igrave;nh lưu niệm trước khu vực Th&aacute;p Đ&ocirc;i (<strong style=\"box-sizing: border-box;\">Twins Tower</strong>) &ndash; Biểu tượng hiện đại của&nbsp;<strong style=\"box-sizing: border-box;\">Malaysia</strong>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Đo&agrave;n d&ugrave;ng cơm chiều v&agrave; về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi.</p>\r\n<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	NG&Agrave;Y 4 : KUALA LUMPUR &ndash; ĐỘNG BATU &ndash; CAO NGUY&Ecirc;N GENTING (ĂN BA BỮA)</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Sau khi d&ugrave;ng bữa s&aacute;ng tại kh&aacute;ch sạn, xe v&agrave; HDV đưa đo&agrave;n tham quan :</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Động Batu &ndash; Th&aacute;nh đường của những người&nbsp;<strong style=\"box-sizing: border-box;\">Malaysia gốc Ấn độ theo đạo Hinđu</strong>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Trung t&acirc;m&nbsp;<strong style=\"box-sizing: border-box;\">v&agrave;ng bạc đ&aacute; qu&yacute; VIP</strong>&nbsp;với đ&aacute; đen (Onyx) phong thủy mang lại may mắn &amp; sức khỏe của Malaysia,Cửa h&agrave;ng miễn thuế, Cửa h&agrave;ng đặc sản địa phương.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Đo&agrave;n d&ugrave;ng bữa trưa tại nh&agrave; h&agrave;ng địa phương.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Tiếp tục h&agrave;nh tr&igrave;nh đến&nbsp;<strong style=\"box-sizing: border-box;\">Cao nguy&ecirc;n Genting</strong>, l&ecirc;n đỉnh Cao Nguy&ecirc;n Genting ở độ cao 1.800m bằng c&aacute;p treo hiện đại. Qu&yacute; kh&aacute;ch tự do tham quan v&agrave; chơi c&aacute;c tr&ograve; chơi tại C&ocirc;ng vi&ecirc;n Indoor/outdoor theme park (<strong style=\"box-sizing: border-box;\">chi ph&iacute; tự t&uacute;c</strong>), hoặc thử vận may tại&nbsp;<strong style=\"box-sizing: border-box;\">Casino de Genting</strong>&nbsp;nổi tiếng Ch&acirc;u &Aacute;.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; text-align: center; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<img alt=\"\" height=\"399\" src=\"https://cdn2.ivivu.com/2017/06/01/16/ivivu-cao-nguyen-genting.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; margin: 0px auto; display: block; max-width: 100%; height: auto;\" width=\"710\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	D&ugrave;ng cơm chiều, về lại kh&aacute;ch sạn ở&nbsp;<strong style=\"box-sizing: border-box;\">Kuala Lumpur</strong>&nbsp;nghỉ ngơi.</p>\r\n<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	NG&Agrave;Y 5 : KUALA LUMPUR &ndash; PUTRAJAYA (NEW CITY) - TP.HCM ( ĂN S&Aacute;NG )</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Ăn s&aacute;ng tại kh&aacute;ch sạn. L&agrave;m thủ tục trả ph&ograve;ng kh&aacute;ch sạn. Xe đưa qu&yacute; kh&aacute;ch tham quan&nbsp;<strong style=\"box-sizing: border-box;\">Th&agrave;nh phố mới Putrajaya</strong>&nbsp;với c&aacute;c c&ocirc;ng tr&igrave;nh hiện đại:</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; text-align: center; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<img alt=\"\" height=\"399\" src=\"https://cdn2.ivivu.com/2017/06/01/16/ivivu-malaysia-thanh-pho-putrajaya.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; margin: 0px auto; display: block; max-width: 100%; height: auto;\" width=\"710\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	-&nbsp;<strong style=\"box-sizing: border-box;\">Quảng trường, Dinh Thủ Tướng, Đền thờ Hồi Gi&aacute;o</strong>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Tự do dạo chơi mua sắm đến giờ xe đưa đo&agrave;n ra phi trường l&agrave;m thủ tục đ&aacute;p chuyến bay về&nbsp;<strong style=\"box-sizing: border-box;\">Việt Nam</strong>.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Đến s&acirc;n bay T&acirc;n Sơn Nhất, Hướng dẫn vi&ecirc;n trợ gi&uacute;p h&agrave;nh kh&aacute;ch l&agrave;m thủ tục nhập cảnh v&agrave; lấy h&agrave;nh l&yacute;. Tạm biệt qu&yacute; kh&aacute;ch v&agrave; hẹn gặp lại trong những h&agrave;nh tr&igrave;nh kh&aacute;c.</p>\r\n', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Ph&ograve;ng đơn : 4.690.000/kh&aacute;ch (4 đ&ecirc;m)</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	-&nbsp;Phụ thu (Seagames) c&aacute;c ng&agrave;y khởi h&agrave;nh trong th&aacute;ng 08 : 500.000/kh&aacute;ch</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Trẻ em dưới 2 tuổi : 3.000.000/kh&aacute;ch</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Trẻ em từ 2 tuổi tới dưới 11 tuổi : 90% gi&aacute; v&eacute; người lớn.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Hai người lớn được k&egrave;m 1 trẻ em,trẻ thứ 2 mua v&eacute; như người lớn.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Trẻ em tr&ecirc;n 11 tuổi : mua v&eacute; người lớn.</p>\r\n');
INSERT INTO `tours` VALUES ('4', 'Tour Singapore 4N3D: Đảo Sentosa - Sea Aquarium', 'BTO104', 0x746F75722073696E6761706F726520346E33643A20C491E1BAA36F2073656E746F7361202D2073656120617175617269756D2020746F75722073696E6761706F726520346E33643A2064616F2073656E746F7361202D2073656120617175617269756D20, '5', '1', '0', '3700000', '0', '2600000', '1', '1', 'Thứ 5 hằng tuần', 'Bao gồm vé xe lửa', '1', '1', '1', '1', '0', '1', '11', '11', '', '<p>\r\n	mo tả</p>\r\n', '2,3,4,1', '1,2', '4,1,2', '1508156474_fxv1508156474.jpg', 'W3sibmFtZSI6IjE1MDcxOTQzMTZfdHRsMTUwNzE5NDMxNi5qcGcifSx7Im5hbWUiOiIxNTA4MTU2NDc0X2Z4djE1MDgxNTY0NzQuanBnIn1d', 'Khách sạn 3 sao | Gồm hành lý ký gửi | Bay VietJet', null, null, null, '1', '0', '1507188806', '1508156634', null, null, '0', '112', '22', '22', '222', '<p>\r\n	Gi&aacute; bao gồm</p>\r\n', '<p>\r\n	lịch tr&igrave;nh</p>\r\n', '<p>\r\n	Th&ocirc;ng tin phụ thu</p>\r\n');
INSERT INTO `tours` VALUES ('5', 'Tour Campuchia 4N3Đ: Bokor - Shihanouk Ville - Kohrong - Phnôm Pênh (Deal)', 'BTO105', , '4', '1,2', '0', '3500000', '0', '2000000', '1', '1', 'Thứ 5 hằng tuần.Tiêu chuẩn khách sạn 5,3,3 sao.', 'Deal tốt giá shock', '0', '0', '0', '1', '0', 'Tour Campuchia 4N3Đ: Bokor - Shihanouk Ville - Kohrong - Phnôm Pênh (Deal)', 'Tour Campuchia 4N3Đ: Bokor - Shihanouk Ville - Kohrong - Phnôm Pênh (Deal)', 'Tour Campuchia 4N3Đ: Bokor - Shihanouk Ville - Kohrong - Phnôm Pênh (Deal)', 'Tour Campuchia 4N3Đ: Bokor - Shihanouk Ville - Kohrong - Phnôm Pênh (Deal)', '', '2,3,4,1', '1,2', '3,4,1', '1507194159_ewx1507194159.jpg', 'W3sibmFtZSI6IjE1MDcxOTQxNTlfZ2xzMTUwNzE5NDE1OS5qcGcifSx7Im5hbWUiOiIxNTA3MTk0MTU5X2V3eDE1MDcxOTQxNTkuanBnIn1d', 'KH thứ 5 hàng tuần|Khách sạn 5 sao|Kohrong Saloem', null, null, null, '1', '0', '1507190495', '1508156655', null, null, '0', 'Công ty du lịch Nam Á Châu', '', '', '219 - 221 Phạm Ngũ Lão,Q.1,Tp.HCM', '', '<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	ĐIỂM NỔI BẬT</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- NGHỈ DƯỠNG TẠI THUNSAR BOKOR 5 SAO</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- THƯỞNG THỨC BỮA TỐI TẠI THUNSAR BOKOR 5 SAO</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- THAM QUAN B&Atilde;I BIỂN ĐẸP NHẤT KOHRONG SALOEM</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- LẨU SOU SOU TỰ CHỌN TẠI NH&Agrave; H&Agrave;NG LẨU LỚN NHẤT PHNOM PENH</p>\r\n<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	NG&Agrave;Y 01: HỒ CH&Iacute; MINH&nbsp;&ndash; &nbsp;CAO NGUY&Ecirc;N BOKOR (S&Aacute;NG, TRƯA, TỐI)</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	5h00 s&aacute;ng: Xe v&agrave; hướng dẫn của C&ocirc;ng ty đ&oacute;n kh&aacute;ch tại điểm hẹn khởi h&agrave;nh đi&nbsp;<strong style=\"box-sizing: border-box;\">Campuchia</strong>. D&ugrave;ng điểm t&acirc;m tại&nbsp;<strong style=\"box-sizing: border-box;\">Trảng B&agrave;ng (đặc sản b&aacute;nh canh gi&ograve; heo)</strong>. Đến cửa khẩu Mộc B&agrave;i l&agrave;m thủ tục xuất nhập cảnh. Tiếp tục h&agrave;nh tr&igrave;nh tới Thủ đ&ocirc; Phnom Penh. &nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Buổi trưa: Đo&agrave;n d&ugrave;ng cơm trưa tại nh&agrave; h&agrave;ng sau đ&oacute; Tiếp tục h&agrave;nh tr&igrave;nh chinh phục&nbsp;<strong style=\"box-sizing: border-box;\">Cao Nguy&ecirc;n Bokor</strong>&nbsp;(độ cao 1080m so với mặt nước biển), với hệ thống đường uốn lượn tuyệt đẹp &ndash; từ đ&acirc;y c&oacute; thể ph&oacute;ng tầm mắt nh&igrave;n to&agrave;n&nbsp;<strong style=\"box-sizing: border-box;\">Vịnh Thailand v&agrave; Đảo Ph&uacute; Quốc</strong>&nbsp;(Việt Nam)&hellip;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; text-align: center; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<img alt=\"\" height=\"399\" src=\"https://cdn2.ivivu.com/2017/07/07/10/ivivu-cao-nguyen-bokor.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; margin: 0px auto; display: block; max-width: 100%; height: auto;\" width=\"710\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Sau đ&oacute; tiếp tục h&agrave;nh tr&igrave;nh đến với thi&ecirc;n đường&nbsp;<strong style=\"box-sizing: border-box;\">Cao Nguy&ecirc;n Bokor</strong>. Đến Bokor đo&agrave;n nhận ph&ograve;ng nghỉ ngơi tại&nbsp;<strong style=\"box-sizing: border-box;\">kh&aacute;ch sạn Thansur Bokor Highland Resort 5 sao.</strong></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Buổi tối: Đo&agrave;n d&ugrave;ng&nbsp;<strong style=\"box-sizing: border-box;\">cơm tối tại kh&aacute;ch sạn Thunsar Bokor 5 sao.</strong></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Qu&yacute; kh&aacute;ch tự do kh&aacute;m ph&aacute; cao nguy&ecirc;n Bokor v&agrave; thử vận may tại Casino.</p>\r\n<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	NG&Agrave;Y 02: BOKOR -&nbsp; SIHANOUK VILLE - KOHRONG (S&Aacute;NG, TRƯA, TỐI)</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Buổi s&aacute;ng: D&ugrave;ng bữa s&aacute;ng buffet tại kh&aacute;ch sạn. L&agrave;m thủ tục trả ph&ograve;ng.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Đo&agrave;n tham quan: nơi nghỉ v&agrave; sinh hoạt của&nbsp;<strong style=\"box-sizing: border-box;\">Vua Shihanouk</strong>&nbsp;từng đi săn bắn tại nơi đ&acirc;y, chụp h&igrave;nh&nbsp;<strong style=\"box-sizing: border-box;\">Cung điện - Casino, Nh&agrave; Thờ C&ocirc;ng Gi&aacute;o</strong>&nbsp;do thực d&acirc;n Ph&aacute;p x&acirc;y dựng những năm 1917. Đặc biệt viếng Ch&ugrave;a Năm Thuyền &ndash; nghe kể truyền thuyết của&nbsp;<strong style=\"box-sizing: border-box;\">Ho&agrave;ng Tử Preah Thom v&agrave; C&ocirc;ng ch&uacute;a Thủy Cung Nagani</strong>&nbsp;&ndash; nơi khởi đầu của nền văn minh Khmer&hellip;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Sau đ&oacute; xe đưa đo&agrave;n đến th&agrave;nh phố biển&nbsp;<strong style=\"box-sizing: border-box;\">Sihanouk Ville.</strong>&nbsp;Đến Shihanouk đo&agrave;n d&ugrave;ng cơm trưa.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Sau đ&oacute; xe đưa Qu&yacute; kh&aacute;ch ra cảng khởi h&agrave;nh đến vui chơi tại đảo&nbsp;<strong style=\"box-sizing: border-box;\">Koh Rong Saloem</strong>, được mệnh danh l&agrave; b&atilde;i biển đẹp nhất Đ&ocirc;ng Nam &Aacute;. Đến đ&acirc;y, Qu&yacute; kh&aacute;ch tự do tắm biển kh&aacute;m ph&aacute; đảo Kohrong Saloem.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; text-align: center; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<img alt=\"\" height=\"399\" src=\"https://cdn2.ivivu.com/2017/07/06/14/ivivu-koh-rong-cambodia.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; margin: 0px auto; display: block; max-width: 100%; height: auto;\" width=\"710\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	16h00: Qu&yacute; kh&aacute;ch l&ecirc;n t&agrave;u về lại th&agrave;nh phố biển&nbsp;<strong style=\"box-sizing: border-box;\">Shihanouk Ville</strong>. Về kh&aacute;ch sạn nhận ph&ograve;ng.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Đo&agrave;n d&ugrave;ng cơm tối, sau đ&oacute; tự do dạo biển ngắm nh&igrave;n những tia ph&aacute;o hoa vụt s&aacute;ng tr&ecirc;n bầu trời hoặc lồng đ&egrave;n được đốt s&aacute;ng thả l&ecirc;n trời&hellip; Nghỉ đ&ecirc;m tại Sihanouk.</p>\r\n<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	NG&Agrave;Y 03: SHIHANOUK VILLE &ndash; PHNOM PENH (S&Aacute;NG, TRƯA, CHIỀU)</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	7:00 D&ugrave;ng bữa s&aacute;ng Buffet tại kh&aacute;ch sạn. L&agrave;m thủ tục trả ph&ograve;ng.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Sau đ&oacute; đo&agrave;n khởi h&agrave;nh về lại thủ đ&ocirc; Phnom Penh. Tham quan, mua sắm tại chợ Campot v&agrave; khởi h&agrave;nh về Phnompenh.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; text-align: center; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	<img alt=\"\" height=\"399\" src=\"https://cdn2.ivivu.com/2017/07/06/14/ivivu-phnom-penh.jpg\" style=\"box-sizing: border-box; border: 0px; vertical-align: middle; margin: 0px auto; display: block; max-width: 100%; height: auto;\" width=\"710\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	- Buổi trưa: Đo&agrave;n đến Phnom penh d&ugrave;ng cơm trưa. Sau đ&oacute; về kh&aacute;ch sạn nhận ph&ograve;ng nghỉ ngơi.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Buổi chiều: Đo&agrave;n d&ugrave;ng bữa tối. Sau đ&oacute; tham quan v&agrave; thử vận may tại Casino Naga World 5 Sao sang trọng nhất Cambodia.Về kh&aacute;ch sạn, tự do kh&aacute;m ph&aacute; thủ đ&ocirc; Phnom Penh.</p>\r\n<h3 style=\"box-sizing: border-box; font-family: Helvetica, Arial, sans-serif; line-height: 1.5; color: rgb(78, 78, 78); margin-top: 20px; margin-bottom: 10px; font-size: 18px; text-transform: uppercase;\">\r\n	&nbsp;NG&Agrave;Y 04: PHNOM PENH &ndash; HỒ CH&Iacute; MINH (S&Aacute;NG, TRƯA)</h3>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	7h00: D&ugrave;ng bữa s&aacute;ng Buffet tại kh&aacute;ch sạn, l&agrave;m thủ tục trả ph&ograve;ng. Sau đ&oacute;, tham quan chợ Mới mua đặc sản Campuchia.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; line-height: 1.75; font-size: 16px; color: rgb(51, 51, 51); font-family: Helvetica, Arial, sans-serif;\">\r\n	Đo&agrave;n Khởi h&agrave;nh về lại Tp. Hồ Ch&iacute; Minh. HDV C&ocirc;ng Ty tiễn kh&aacute;ch về điểm khởi h&agrave;nh ban đầu. Kết th&uacute;c tour, chia tay v&agrave; hẹn gặp lại Qu&yacute; kh&aacute;ch.</p>\r\n', '');
