  `top_id` int(11) NOT NULL AUTO_INCREMENT,
  `top_pictures` varchar(255) DEFAULT NULL,
  `ppic_note` varchar(255) DEFAULT NULL,
  `top_tour_id` int(11) DEFAULT '0',
  `top_order` int(11) DEFAULT '0',
  `top_temp_key` varchar(255) DEFAULT NULL,
  `top_main_pic` tinyint(1) DEFAULT '0',
  PRIMARY KEY (`top_id`),
  KEY `top_tour_id` (`top_tour_id`) USING BTREE,
  KEY `top_order` (`top_order`) USING BTREE
) ENGINE=MyISAM DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

