-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `use_id` int(11) NOT NULL AUTO_INCREMENT,
  `use_login_name` varchar(100) DEFAULT NULL,
  `use_password` varchar(100) DEFAULT NULL,
  `use_secrest` varchar(10) NOT NULL,
  `use_fullname` varchar(100) DEFAULT NULL,
  `use_date_join` int(11) DEFAULT '0',
  `use_active` tinyint(1) DEFAULT '1',
  `use_hot` tinyint(1) DEFAULT '0',
  `use_birthday` int(11) DEFAULT '0',
  `use_birthday_year` int(11) DEFAULT '0',
  `use_city_id` int(11) DEFAULT '0',
  `use_school_id` int(11) DEFAULT '0',
  `use_relocate` int(11) DEFAULT '0',
  `use_age` int(11) DEFAULT '0',
  `use_gender` tinyint(1) DEFAULT '0' COMMENT '0 Nữ, 1 Nam',
  `use_address` varchar(255) DEFAULT NULL,
  `use_born_address` varchar(255) DEFAULT NULL,
  `use_born_lat_lon` varchar(100) DEFAULT NULL,
  `use_branch` varchar(255) DEFAULT NULL COMMENT 'chức vụ đang làm',
  `use_fbid` varchar(30) DEFAULT NULL,
  `use_check_friend` tinyint(1) DEFAULT '0',
  `use_profile_social` varchar(255) DEFAULT NULL,
  `use_avatar` varchar(255) DEFAULT NULL,
  `use_social_type` varchar(20) DEFAULT NULL,
  `use_com_id` int(11) DEFAULT '0' COMMENT 'id công ty đang làm việc',
  `use_is_verify_email` int(1) DEFAULT '0' COMMENT 'đã xác thực email',
  `use_is_verify_phone` int(1) DEFAULT '0' COMMENT 'đã xác thực số điện thoại',
  `use_type` int(1) DEFAULT '0' COMMENT '0: tài khoản ứng viên, 1: tài khoản tuyển dụng',
  `use_phone` varchar(30) DEFAULT NULL,
  `use_email` varchar(100) DEFAULT NULL,
  `use_email_notifi` varchar(100) DEFAULT NULL,
  `use_process` int(11) DEFAULT '0' COMMENT 'tỷ lệ hoàn thành hồ sơ',
  `use_cv_name` varchar(100) DEFAULT NULL,
  `use_cat_id` int(11) DEFAULT '0',
  `use_cat_id_1` int(11) DEFAULT '0',
  `use_cv_template` varchar(50) DEFAULT NULL,
  `use_cv_count` int(11) DEFAULT '0',
  `use_avatar_cv` varchar(100) DEFAULT NULL,
  `use_experience_id` int(11) DEFAULT '0',
  `use_level` int(11) DEFAULT '0',
  `use_married` int(11) DEFAULT '0',
  `use_company_id` int(11) DEFAULT '0',
  `use_money` double DEFAULT NULL,
  `use_status` int(11) DEFAULT '0',
  `use_lang_cv` varchar(10) DEFAULT 'vi',
  `use_job_fb_count` int(11) NOT NULL DEFAULT '0',
  `use_shop_name` varchar(255) DEFAULT NULL,
  `use_shop_address` varchar(255) DEFAULT NULL,
  `use_shop_title` varchar(255) DEFAULT NULL,
  `use_cookie_id` varchar(40) DEFAULT NULL,
  `use_estore_type` tinyint(2) NOT NULL DEFAULT '0',
  `use_right` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`use_id`),
  KEY `use_active` (`use_active`),
  KEY `use_city_id` (`use_city_id`),
  KEY `use_login_name` (`use_login_name`),
  KEY `use_phone` (`use_phone`),
  KEY `use_email` (`use_email`),
  KEY `use_job_fb_count` (`use_job_fb_count`),
  KEY `use_shop_name` (`use_shop_name`),
  KEY `use_cookie_id` (`use_cookie_id`),
  KEY `use_estore_type` (`use_estore_type`),
  KEY `use_right` (`use_right`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

